import React, { useState, useRef } from 'react';
import { fabric } from 'fabric';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCog, faImage, faTextWidth, faShapes, faUpload, faImages, faAlignLeft, faAlignCenter, faAlignRight } from '@fortawesome/free-solid-svg-icons';
import ColorPicker from './ColorPicker'; // Import your color picker component
import LogosPanel from './LogosPanel';
import BackgroundsPanel from './BackgroundsPanel';
import ShapeIcon from './ShapeIcon';

const Sidebar = ({ canvas, textProperties, setTextProperties, selectedText, setSelectedText, logos, backgrounds, handleSelectLogo, handleSelectBackground, fonts }) => {
  const [activePanel, setActivePanel] = useState(null);
  const [canvasWidth, setCanvasWidth] = useState(canvas.width);
  const [canvasHeight, setCanvasHeight] = useState(canvas.height);
  const fileInputRef = useRef(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTextProperties((prevProps) => ({
      ...prevProps,
      [name]: value,
    }));

    if (selectedText) {
      selectedText.set(name, value);
      canvas.renderAll();
    }
  };

  const handleAlignText = (alignment) => {
    if (selectedText) {
      selectedText.set({ textAlign: alignment });
      canvas.renderAll();
    }
  };

  const handleAddText = () => {
    const text = new fabric.IText("New Text", {
      left: 100,
      top: 100,
      fontFamily: textProperties.fontFamily,
      fontSize: parseInt(textProperties.fontSize),
      fontWeight: textProperties.fontWeight,
      fontStyle: textProperties.fontStyle,
      underline: textProperties.underline,
      fill: textProperties.color,
    });

    canvas.add(text);
    canvas.setActiveObject(text);
    setSelectedText(text);
    setActivePanel('text');
  };

  const handleAddShape = (shape) => {
    let newShape;
    const shapeProperties = {
      left: 100,
      top: 100,
      fill: textProperties.color, // Assuming color should be applied
    };

    switch (shape) {
      case 'rectangle':
        newShape = new fabric.Rect({ ...shapeProperties, width: 100, height: 60 });
        break;
      case 'circle':
        newShape = new fabric.Circle({ ...shapeProperties, radius: 50 });
        break;
      case 'triangle':
        newShape = new fabric.Triangle({ ...shapeProperties, width: 100, height: 100 });
        break;
      case 'line':
        newShape = new fabric.Line([50, 100, 200, 200], { stroke: textProperties.color, strokeWidth: 5 });
        break;
      case 'diamond':
        newShape = new fabric.Polygon([
          { x: 50, y: 0 },
          { x: 100, y: 50 },
          { x: 50, y: 100 },
          { x: 0, y: 50 },
        ], { ...shapeProperties });
        break;
      default:
        return;
    }

    if (newShape) {
      canvas.add(newShape);
      canvas.setActiveObject(newShape);
      setActivePanel('shape');
    }
  };

  const handleColorChange = (color) => {
    setTextProperties((prevProps) => ({
      ...prevProps,
      color: color.hex,
    }));
    if (selectedText) {
      selectedText.set("fill", color.hex);
      canvas.renderAll();
    }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = function (f) {
      const data = f.target.result;
      fabric.Image.fromURL(data, function (img) {
        const oImg = img.set({
          left: 0,
          top: 0,
          angle: 0,
          scaleX: canvas.width / img.width,
          scaleY: canvas.height / img.height,
        }).scale(0.9);
        canvas.add(oImg).renderAll();
        canvas.setActiveObject(oImg);
      });
    };
    reader.readAsDataURL(file);
  };

  const handleClick = () => {
    fileInputRef.current.click();
  };

  return (
    <div className="sidebar">
      <div className="panel">
        <div className="panel-header">
          <h3>Toolbar</h3>
          <button onClick={() => setActivePanel(activePanel === 'settings' ? null : 'settings')}>
            <FontAwesomeIcon icon={faCog} />
          </button>
        </div>
        <div className="panel-content">
          <button onClick={() => setActivePanel(activePanel === 'settings' ? null : 'settings')}>
            <FontAwesomeIcon icon={faCog} /> Customize
          </button>
          <button onClick={() => setActivePanel(activePanel === 'backgrounds' ? null : 'backgrounds')}>
            <FontAwesomeIcon icon={faImage} /> Backgrounds
          </button>
          <button onClick={handleAddText}>
            <FontAwesomeIcon icon={faTextWidth} /> Text
          </button>
          <button onClick={() => setActivePanel(activePanel === 'logos' ? null : 'logos')}>
            <FontAwesomeIcon icon={faImages} /> Logos
          </button>
          <button onClick={handleClick}>
            <FontAwesomeIcon icon={faUpload} /> Upload
          </button>
          <button onClick={() => setActivePanel(activePanel === 'shapes' ? null : 'shapes')}>
            <FontAwesomeIcon icon={faShapes} /> Shapes
          </button>
          <input type="file" ref={fileInputRef} style={{ display: 'none' }} onChange={handleFileUpload} />
        </div>
      </div>

      {activePanel === 'settings' && (
        <div className="panel">
          <div className="panel-header">
            <h3>Settings</h3>
            <button onClick={() => setActivePanel(null)}>Close</button>
          </div>
          <div className="panel-content">
            <div className="form-group">
              <label>Canvas Width:</label>
              <input
                type="number"
                value={canvasWidth}
                onChange={(e) => setCanvasWidth(parseInt(e.target.value))}
              />
            </div>
            <div className="form-group">
              <label>Canvas Height:</label>
              <input
                type="number"
                value={canvasHeight}
                onChange={(e) => setCanvasHeight(parseInt(e.target.value))}
              />
            </div>
          </div>
        </div>
      )}

      {activePanel === 'backgrounds' && (
        <BackgroundsPanel backgrounds={backgrounds} onSelectBackground={handleSelectBackground} />
      )}

      {activePanel === 'text' && (
        <div className="panel">
          <div className="panel-header">
            <h3>Text Options</h3>
            <button onClick={() => setActivePanel(null)}>Close</button>
          </div>
          <div className="panel-content">
            <div className="form-group">
              <label>Text:</label>
              <input
                type="text"
                name="text"
                value={textProperties.text}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Font Family:</label>
              <select
                name="fontFamily"
                value={textProperties.fontFamily}
                onChange={handleChange}
              >
                {fonts.map((font) => (
                  <option key={font} value={font}>
                    {font}
                  </option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>Font Size:</label>
              <input
                type="number"
                name="fontSize"
                value={textProperties.fontSize}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Color:</label>
              <ColorPicker color={textProperties.color} onChange={handleColorChange} />
            </div>
            <div className="form-group">
              <label>Font Weight:</label>
              <select
                name="fontWeight"
                value={textProperties.fontWeight}
                onChange={handleChange}
              >
                <option value="normal">Normal</option>
                <option value="bold">Bold</option>
              </select>
            </div>
            <div className="form-group">
              <label>Font Style:</label>
              <select
                name="fontStyle"
                value={textProperties.fontStyle}
                onChange={handleChange}
              >
                <option value="normal">Normal</option>
                <option value="italic">Italic</option>
              </select>
            </div>
            <div className="form-group">
              <label>Underline:</label>
              <select
                name="underline"
                value={textProperties.underline}
                onChange={handleChange}
              >
                <option value="false">No</option>
                <option value="true">Yes</option>
              </select>
            </div>
            <div className="form-group">
              <label>Text Alignment:</label>
              <div className="text-alignment-buttons">
                <button onClick={() => handleAlignText('left')}>
                  <FontAwesomeIcon icon={faAlignLeft} />
                </button>
                <button onClick={() => handleAlignText('center')}>
                  <FontAwesomeIcon icon={faAlignCenter} />
                </button>
                <button onClick={() => handleAlignText('right')}>
                  <FontAwesomeIcon icon={faAlignRight} />
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {activePanel === 'logos' && <LogosPanel logos={logos} onSelectLogo={handleSelectLogo} />}

      {activePanel === 'shapes' && (
        <div className="panel">
          <div className="panel-header">
            <h3>Shape Options</h3>
            <button onClick={() => setActivePanel(null)}>Close</button>
          </div>
          <div className="panel-content">
            <div className="form-group">
              <label>Add Shape:</label>
              <div className="shape-buttons">
                <ShapeIcon shape="rectangle" onClick={() => handleAddShape('rectangle')} />
                <ShapeIcon shape="circle" onClick={() => handleAddShape('circle')} />
                <ShapeIcon shape="triangle" onClick={() => handleAddShape('triangle')} />
                <ShapeIcon shape="line" onClick={() => handleAddShape('line')} />
                <ShapeIcon shape="diamond" onClick={() => handleAddShape('diamond')} />
              </div>
            </div>
            <div className="form-group">
              <label>Color:</label>
              <ColorPicker color={textProperties.color} onChange={handleColorChange} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
