import axios from "axios";

export const generateCaption = async (caption, task) => {
  try {
   var prompt =
      "act as a caption modifier and give " +
      task +
      " of caption with respect to :-" +
      caption +
      "and only give the caption no extra info needed";
    const response = await axios.post("http://localhost:3002/api/generate/", {
      model: "llama3",
      prompt: prompt,
      stream: false,
    });
    return response.data;
  } catch (error) {
    console.error("There was a problem with the axios operation:", error);
    throw error;
  }
};
